    console.log(this)
    console.log('Clicou')
  }

  render(){
    return (
      <>
        <button key={'botão1'} onClick={this.handleClick}>
          Botão1
        </button>
        <button key='botão2' onClick={(evt) => this.handleClick(evt.target.innerText)}>
          Botão2
        </button>
        <button key='botão3' onClick={(evt) => this.handleClick(evt.target.innerText)}>
          Botão3
        </button>
    </>
    )
  }
}

