import react from "react";

class DadJoke extends react.Component{
  constructor(){
    super()
    this.state= {
      jokeObj: undefined,
      loading:true,
      arrayOfJokes: [],

    }
  }

  fetchJoke = async () => {
    const URL_API = "https://icanhazdadjoke.com/";
    const HEADERS_API =  { headers: { Accept: 'application/json' } }

    const request = await fetch(URL_API, HEADERS_API);
    const requestObj = await request.json();

    this.setState({
      jokeObj: requestObj,
    })
  }

  componentDidMount = () => {
    this.fetchJoke();
  }

  saveJoke = () => {
    this.state(({ arrayOfJokes, jokeObj })=> ({
      arrayOfJokes: [...arrayOfJokes, jokeObj],
    }))
    this.fetchJoke();
    console.log('entrou')
  }

  renderJokeElement = () => {
    return (
      <div>
      <p>{this.state.jokeObj.joke}</p>
      <button type="button" onClick={this.saveJoke}>
        Salvar Piada
      </button>
    </div>
    )
  }


  render(){

    const { jokeObj, arrayOfJokes } = this.state;
    const loadingElement = <span> Loading... </span>;

    return (
      <div>
        <span>
          {arrayOfJokes.map(({id, joke}) => <p key={id}>{joke}</p>)}
        </span>

        <p> {jokeObj ? this.renderJokeElement(): loadingElement}</p>
      </div>
    )
  }
}

export default DadJoke
